/****************************************************************************** 
 * Job:             4 JOB ASSIGN CLUSTER ID INCR            A5HHMQXQ.BB00001U * 
 * Description:                                                               * 
 *                                                                            * 
 * Metadata Server: 192.168.253.166                                           * 
 * Port:            8561                                                      * 
 * Location:        /SASDIR/FINAL/INCR_JOBS                                   * 
 *                                                                            * 
 * Server:          SASApp                                  A5HHMQXQ.AR000002 * 
 *                                                                            * 
 * Generated on:    Thursday, January 30, 2014 12:13:29 PM GMT+05:30          * 
 * Generated by:    sasdemo                                                   * 
 * Version:         SAS Data Integration Studio 4.21                          * 
 ******************************************************************************/ 

/* General macro variables  */ 
%let jobID = %quote(A5HHMQXQ.BB00001U);
%let etls_jobName = %nrquote(4 JOB ASSIGN CLUSTER ID INCR);
%let etls_userID = %nrquote(sasdemo);

/* Setup to capture return codes  */ 
%global job_rc trans_rc sqlrc; 
%let sysrc = 0;
%let job_rc = 0;
%let trans_rc = 0;
%let sqlrc = 0;
%global etls_stepStartTime; 
/* initialize syserr to 0 */
data _null_; run;

%macro rcSet(error); 
   %if (&error gt &trans_rc) %then 
      %let trans_rc = &error;
   %if (&error gt &job_rc) %then 
      %let job_rc = &error;
%mend rcSet; 

%macro rcSetDS(error); 
   if &error gt input(symget('trans_rc'),12.) then 
      call symput('trans_rc',trim(left(put(&error,12.))));
   if &error gt input(symget('job_rc'),12.) then 
      call symput('job_rc',trim(left(put(&error,12.))));
%mend rcSetDS; 

/* Create metadata macro variables */
%let IOMServer      = %nrquote(SASApp);
%let metaPort       = %nrquote(8561);
%let metaServer     = %nrquote(192.168.253.166);

/* Set metadata options */
options metaport       = &metaPort 
        metaserver     = "&metaServer"; 

/* Setup for capturing job status  */ 
%let etls_startTime = %sysfunc(datetime(),datetime.);
%let etls_recordsBefore = 0;
%let etls_recordsAfter = 0;
%let etls_lib = 0;
%let etls_table = 0;

%global etls_debug; 
%macro etls_setDebug; 
   %if %str(&etls_debug) ne 0 %then 
      OPTIONS MPRINT%str(;); 
%mend; 
%etls_setDebug; 

/*---- Start of Pre-Process Code  ----*/ 

/*---- Start of User Written Code  ----*/ 


/* Count of dedupe match codes */
%let mc_cnt=2;

/* Parameters for clustring user written code */
%let opdsn=saslib.CLUSTERED_OUTPUT;
%let seq_dsn_temp=work.CLUSTER_ID_GEN_TEMP;
%let seq_dsn=saslib.CLUSTER_ID_GENERATOR;
%let seq_col=CLUSTER_ID_SEQ;
%let cl_id_col=CLUSTER_ID;
%let by_col_1=DEDUPE_MATCH_CODE_1;
%let by_col_2=DEDUPE_MATCH_CODE_2;
/*%let by_col_3=DEDUPE_MATCH_CODE_3;*/
/*%let by_col_4=DEDUPE_MATCH_CODE_4;*/

/*%let by_col_5=DEDUPE_MATCH_CODE_5;*/
/*%let by_col_6=DEDUPE_MATCH_CODE_6;*/



/* This macro checks if the table SASLIB.Sas_Update_Date exists. 
	If no, then it creates one in the DataMart library	*/

%macro Update_Date();

/* Determine if the SAS_UPDATE_DATE table exists  */ 
%let upd_tbl_Exist = %sysfunc(exist(saslib.SAS_UPDATE_DATE, DATA));

/*---- Create table  ----*/ 
%if (&upd_tbl_Exist eq 0) %then 
%do;  /* if the table does not exist  */ 

	%put %str(NOTE: Creating table ...);

	PROC SQL;
	CREATE TABLE saslib.SAS_UPDATE_DATE (TABLE_NAME VARCHAR(25), LAST_UPDATE_DATE DATE);
	QUIT;

		proc sql noprint;

			insert into saslib.Sas_Update_Date(Table_Name,Last_Update_Date)
			values ('SAS_DIM_DEDUPE',0);
		quit;

	%end; /* if the table does not exist  */

%mend Update_Date;


/* Call the macro Update_Date */

%Update_Date;

data saslib.new_old_cluster_id_xref;	
set saslib.new_old_cluster_id_xref(obs=0);	
Output;
Run;



/*---- End of User Written Code  ----*/
/*---- End of Pre-Process Code  ----*/ 

%rcSet(&syserr); 
%rcSet(&sqlrc); 

/*---- Start of User Written Code  ----*/ 

/*============================================================================* 
 * Step:          User Written Code                         A5VKCMXH.AR0001L1 * 
 * Transform:     User Written Code                                           * 
 * Description:                                                               * 
 *                                                                            * 
 * Source Table:  Extract Target - work.W5P7Z7MJ            A5VKCMXH.AU0001KS * 
 * Target Table:  User Written Code Target -                A5VKCMXH.AU0001KQ * 
 *                 svoclib.CLUSTERED_OUTPUT                                   * 
 *                                                                            * 
 * User Written:  UWC_CLUSTER_ID_ASSIGN                     A5VKCMXH.A90002O3 * 
 *============================================================================*/ 

%let SYSLAST = %nrquote(saslib.SAS_DIM_DEDUPE); 
/*%let SYSLAST = %nrquote(saslib.test_cluster_input2); */

%let transformID = %quote(A5VKCMXH.AR0001L1); 

%let trans_rc = 0; 

%let _OUTPUT = %nrquote(saslib.CLUSTERED_OUTPUT); 


/*---- Start of User Written Code  ----*/ 

%let ipdsn=&syslast;

%macro Cluster_Assigner(ipdsn, opdsn, seq_dsn_temp, seq_col, cl_id_col, by_col_1);

/* Determine if the sequence table exists  */ 
%let seq_tbl_Exist = %sysfunc(exist(&seq_dsn_temp, DATA));

/*---- Create a new sequence table  ----*/ 
%if (&seq_tbl_Exist eq 0) %then 
%do;  /* if the sequence table does not exist  */ 

	%put %str(NOTE: Creating table ...);

	data &seq_dsn_temp;
		attrib &seq_col length = 8 format = Best12.;
		&seq_col=0;
	run;

%end; /* if the sequence table does not exist  */

/* Step 1: Separate the input data set into two output data sets. One having missing DMC_1 and 
	other having valid DMC_1 */

data inp_cluster_dmc_1_nl (COMPRESS=YES) inp_cluster_dmc_1_nnl (COMPRESS=YES);
	attrib cluster_id_old length=8. format=Best12.;
	set &ipdsn (keep = SAS_ID CLUSTER_ID DEDUPE_MATCH_CODE_1 DEDUPE_MATCH_CODE_2 
							 DEDUPE_MATCH_CODE_3 DEDUPE_MATCH_CODE_4 DEDUPE_MATCH_CODE_5 DEDUPE_MATCH_CODE_6 
							 DMC_1_FLAG DMC_2_FLAG DMC_3_FLAG DMC_4_FLAG DMC_5_FLAG DMC_6_FLAG BROKERCODE CLIENTCODE);
	sas_id = CATX('!',PUT(BROKERCODE,11.),CLIENTCODE);
	cluster_id_old = cluster_id;
	cluster_id = .;
	if dmc_1_flag eq 0 then
		output inp_cluster_dmc_1_nnl;
	else
		output inp_cluster_dmc_1_nl;
run;

/* Step 2: Sort the data set/view created in the last transformation */

proc sort data=inp_cluster_dmc_1_nnl out=inp_cluster_dmc_1_nnl_srt (COMPRESS=YES) noequals;
	by &by_col_1;
run;

/* Get the last value of sequence number stored in the sequence generator table */

proc sql noprint;
   select &seq_col into :var_seq
	from &seq_dsn_temp;
quit;

%rcSet(&sqlrc);

/* Step 3A: Assign cluster ids to clusters identified by grouping match code column for records
	which have not null DMC_1 match codes */

data out_cluster_dmc_1_nnl (COMPRESS=YES);
	set inp_cluster_dmc_1_nnl_srt (sortedby=&by_col_1);
	by &by_col_1;	/*This Line Does the Gouping @Yug */
	&cl_id_col=symgetn('var_seq');	
	if first.&by_col_1 then do;
		&cl_id_col = &cl_id_col+1;
		call symput('var_seq',&cl_id_col);
	end;
run;

/* Step 3B: Assign unique cluster ids to records with missing DMC_1 match codes */

data out_cluster_dmc_1_nl (COMPRESS=YES);
	set inp_cluster_dmc_1_nl;
	&cl_id_col=symgetn('var_seq');
	&cl_id_col = &cl_id_col+1;
	call symput('var_seq',&cl_id_col);
run;

/*  Store the last generated value of the sequence in the sequence generator table */

proc sql;
	update &seq_dsn_temp
	set &seq_col = &var_seq;
quit;

%rcSet(&sqlrc);

/* Step 4: Append the clustering results achieved using DMC_1 match code into one data set */

data CLUSTER_DMC_1_FINAL (COMPRESS=YES);
	drop &by_col_1;
	attrib var_CLUSTER_id length=8. format=Best12.;
	set out_cluster_dmc_1_nnl
		 out_cluster_dmc_1_nl;
	var_CLUSTER_id=CLUSTER_id;
run;

%mend Cluster_Assigner;

/* Call the clustering macro */

%Cluster_Assigner(&ipdsn, &opdsn, &seq_dsn_temp, &seq_col, &cl_id_col, &by_col_1);

%rcSet(&syserr); 

/* Delete temp data sets created in step # 1 */

proc datasets lib=work nolist nowarn memtype=(data);
   delete inp_cluster_dmc_1_nl inp_cluster_dmc_1_nnl inp_cluster_dmc_1_nnl_srt 
			 out_cluster_dmc_1_nnl out_cluster_dmc_1_nl;
quit;


/*----------------- Macro to do OR-ing between match codes 2 to n ----------------- */

%macro match_code_oring();

%do seq = 2 %to &mc_cnt;

%let prev_seq = %eval(&seq-1);

%put %str(NOTE: Begin OR-ing with match code # &seq);

/*-------------------- OR-ing with match code (Begin) ----------------------------- */

/* Creating view to act as an input to the hash object in memory */

proc sql noprint;
create view cluster_dmc_&seq._hash as
select dedupe_match_code_&seq as dmc_&seq._key, var_CLUSTER_id as CLUSTER_id_data/***/
from cluster_dmc_&prev_seq._final
where dmc_&seq._flag eq 0; /*------ Modified by Yugesh, since 0 without quotes was not working since match_code_flag were character fields------------ */
quit;

%rcSet(&sqlrc);

/* Create a cross-reference of CLUSTER_IDs w.r.t match code */

data inp_cluster_dmc_&seq (COMPRESS=YES);

	drop var_CLUSTER_id CLUSTER_id_data dmc_&seq._key dedupe_match_code_&seq rc;

	attrib CLUSTER_id length=8. format=Best12.;
	attrib CLUSTER_id_&seq length=8. format=Best12.;
	attrib CLUSTER_id_data length=8. format=Best12.;
	attrib dmc_&seq._key length=$200.; /* Modify to suit the length of individual match code field */

	retain CLUSTER_id_&seq;

	set cluster_dmc_&prev_seq._final;

   if _N_ = 1 then 
	do;

		declare hash myhash(hashexp: 20, dataset: "cluster_dmc_&seq._hash");
      myhash.defineKey("dmc_&seq._key");
      myhash.defineData('CLUSTER_id_data');
		myhash.defineDone(); 

%put %str(NOTE: Post Hash);

		call missing(dmc_&seq._key, CLUSTER_id_data);

	end;

	CLUSTER_id = var_CLUSTER_id;

	dmc_&seq._key = dedupe_match_code_&seq;

	rc = myhash.find();

	if (rc = 0) then
		do;
			CLUSTER_id_&seq = CLUSTER_id_data;
		end;	
	else
		do;
			CLUSTER_id_&seq = var_CLUSTER_id;
		end;

run;

%rcSet(&syserr); 

/* sorting the inp_cluster dataset against CLUSTER_id field */

proc sort data=inp_cluster_dmc_&seq;
	by CLUSTER_id;
run;

%rcSet(&syserr); 

/* create a view to act as a input for hash */

data inp_cluster_temp_&seq / view=inp_cluster_temp_&seq ;
 	attrib CLUSTER_id_key length=8. format=Best12.;
 	attrib CLUSTER_id_data length=8. format=Best12.;
	keep CLUSTER_id_key CLUSTER_id_data;
	retain CLUSTER_id_data;
	set inp_cluster_dmc_&seq (sortedby=CLUSTER_id);
	by CLUSTER_id;
	if (last.CLUSTER_id ne 1) then
		do;
			if (CLUSTER_id ne CLUSTER_id_&seq) & (CLUSTER_id_data eq .) then
			do;
				CLUSTER_id_data = CLUSTER_id_&seq;
			end;
		end;

	if (CLUSTER_id ne CLUSTER_id_&seq) then
		do;
			CLUSTER_id_key = CLUSTER_id_&seq;
		end;

	if (CLUSTER_id_key ne .) & (CLUSTER_id_data ne .) & (CLUSTER_id_key ne CLUSTER_id_data) then
		do;
			output;
		end;

	if last.CLUSTER_id then
	do;
		CLUSTER_id_data =.;
	end; 
run;

%rcSet(&syserr); 

/* This step will rectify instances of one old CLUSTER_id having multiple new CLUSTER_id linkages */

data cluster_dmc_&seq._temp (COMPRESS=YES);
	drop CLUSTER_id_key CLUSTER_id_data rc;
 	attrib CLUSTER_id_key length=8. format=Best12.;
 	attrib CLUSTER_id_data length=8. format=Best12.;
   if _N_ = 1 then 
	do;
		declare hash myhash(hashexp: 13, dataset: "inp_cluster_temp_&seq");
      myhash.defineKey('CLUSTER_id_key');
      myhash.defineData('CLUSTER_id_data');
      myhash.defineDone();
		call missing(CLUSTER_id_key, CLUSTER_id_data);
	end;
	set inp_cluster_dmc_&seq;
	CLUSTER_id_key=CLUSTER_id_&seq;
	rc = myhash.find();
	if (rc = 0) then
	do;
		CLUSTER_id_&seq = CLUSTER_id_data;
	end;
run;

%rcSet(&syserr); 

/* Creating view1 to act as an input to the hash object in memory */

proc sql noprint;
 create view inp_cluster_dmc_&seq._v as
	select CLUSTER_id as CLUSTER_id_key, CLUSTER_id_&seq as CLUSTER_id_data 
	from cluster_dmc_&seq._temp
	where CLUSTER_id <> CLUSTER_id_&seq;
quit;

%rcSet(&sqlrc);

/* Creating view2 to act as an input to the hash object in memory */

data cluster_temp_&seq._v / view=cluster_temp_&seq._v;
 	attrib CLUSTER_id_key_2 length=8. format=Best12.;
 	attrib CLUSTER_id_data_2 length=8. format=Best12.;
	keep CLUSTER_id_key_2 CLUSTER_id_data_2;
	retain CLUSTER_id_data_2;
	set cluster_dmc_&seq._temp (sortedby=CLUSTER_id);
	by CLUSTER_id;
	if (last.CLUSTER_id ne 1) then
		do;
			if (CLUSTER_id ne CLUSTER_id_&seq) & (CLUSTER_id_data_2 eq .) then
			do;
				CLUSTER_id_data_2 = CLUSTER_id_&seq;
			end;
		end;

	if (CLUSTER_id ne CLUSTER_id_&seq) then
		do;
			CLUSTER_id_key_2 = CLUSTER_id_&seq;
		end;

	if (CLUSTER_id_key_2 ne .) & (CLUSTER_id_data_2 ne .) & (CLUSTER_id_key_2 ne CLUSTER_id_data_2) then
		do;
			output;
		end;

	if last.CLUSTER_id then
	do;
		CLUSTER_id_data_2 =.;
	end;
run;

/* Re-aligning CLUSTER_IDs for 'n' level of recursive CLUSTER_ID linkage */

data cluster_dmc_&seq._final (COMPRESS=YES);

	drop CLUSTER_id CLUSTER_id_&seq CLUSTER_id_key CLUSTER_id_data CLUSTER_id_key_2 
		  CLUSTER_id_data_2 s_CLUSTER_id s_var_CLUSTER_id rc rc2 rc3 rc4 rc5;

 	attrib var_CLUSTER_id length=8. format=Best12.;
 	attrib CLUSTER_id_key length=8. format=Best12.;
 	attrib CLUSTER_id_data length=8. format=Best12.;
	attrib s_CLUSTER_id length=8. format=Best12.;
	attrib s_var_CLUSTER_id length=8. format=Best12.;
 	attrib CLUSTER_id_key_2 length=8. format=Best12.;
 	attrib CLUSTER_id_data_2 length=8. format=Best12.;	

   if _N_ = 1 then 
	do;
		declare hash myhash(hashexp: 13, dataset: "inp_cluster_dmc_&seq._v");
      myhash.defineKey('CLUSTER_id_key');
      myhash.defineData('CLUSTER_id_data');
      myhash.defineDone();
		call missing(CLUSTER_id_key, CLUSTER_id_data);

		declare hash s_hash(hashexp: 13);
		s_hash.defineKey("s_CLUSTER_id");
		s_hash.defineData("s_var_CLUSTER_id");
		s_hash.defineDone();
		call missing(s_CLUSTER_id, s_var_CLUSTER_id);

		declare hash hash2(hashexp: 13, dataset: "cluster_temp_&seq._v");
      hash2.defineKey('CLUSTER_id_key_2');
      hash2.defineData('CLUSTER_id_data_2');
      hash2.defineDone();
		call missing(CLUSTER_id_key_2, CLUSTER_id_data_2);

	end;

	set cluster_dmc_&seq._temp;

	var_CLUSTER_id=CLUSTER_id_&seq;
	CLUSTER_id_key=CLUSTER_id_&seq;
	rc = myhash.find();

	if (rc = 0) then
		do;
			var_CLUSTER_id=CLUSTER_id_data;
			s_CLUSTER_id=CLUSTER_id_data;

			rc2 = s_hash.find();
			if (rc2 = 0) then
				do;
					var_CLUSTER_id=s_var_CLUSTER_id;
				end;
			else
				do;
					CLUSTER_id_key=CLUSTER_id_data;
					s_var_CLUSTER_id=CLUSTER_id_data;
					rc3 = myhash.find();
					if (rc3 = 0) then
						do;
							var_CLUSTER_id=CLUSTER_id_data; /* last addition */
							s_var_CLUSTER_id=CLUSTER_id_data;
							if (CLUSTER_id_data = CLUSTER_id_&seq) then
								do;
									var_CLUSTER_id = CLUSTER_id_&seq;
								end;
						end;
					s_CLUSTER_id=CLUSTER_id_&seq;
					rc4 = s_hash.add();
				end;
		end;
	else
		do;
			CLUSTER_id_key_2=CLUSTER_id_&seq;
			rc5 = hash2.find();
			if (rc5 = 0) then
				do;
					var_CLUSTER_id=CLUSTER_id_data_2;
				end;
		end;

run;

/*-------------------- OR-ing with match code (End) ----------------------------- */

%rcSet(&syserr); 

%put %str(NOTE: End OR-ing with match code # &seq);

%put %str(NOTE: Deleting temporary work tables);

proc datasets lib=work nolist nowarn;
   delete cluster_dmc_&prev_seq._final inp_cluster_dmc_&seq cluster_dmc_&seq._temp;
quit;

proc datasets lib=work nolist nowarn memtype=(view);
   delete cluster_dmc_&seq._hash inp_cluster_temp_&seq inp_cluster_dmc_&seq._v cluster_temp_&seq._v;
quit;

%end;

%mend match_code_oring;

/* Call the macro to do OR-ing between match codes 2 to n */

%match_code_oring;

/*---------------------------Assign CLUSTER_IDs to clusters-------------------------------*/

%let ipdsn=&syslast;

data _null_;

if &mc_cnt lt 2  then
	call symput('ipdsn','WORK.CLUSTER_DMC_1_FINAL');
else
	call symput('ipdsn',"&syslast");

run;

/* sort on CLUSTER_ID_OLD to resolve any de-linking of clusters */

proc sort data=&ipdsn;
	by CLUSTER_ID_OLD VAR_CLUSTER_ID;
run;

/* create a view to act as a input for hash */

data CLUSTER_DMC_FINAL_V / view=CLUSTER_DMC_FINAL_V ;
 	attrib CLUSTER_id_key length=8. format=Best12.;
 	attrib CLUSTER_id_data length=8. format=Best12.; 
	keep CLUSTER_id_key CLUSTER_id_data;
	retain CLUSTER_id_data;
	set &ipdsn (sortedby=CLUSTER_ID_OLD);
	by CLUSTER_ID_OLD;
	if (CLUSTER_ID_OLD ne .) then
		do;
			if (last.CLUSTER_ID_OLD ne 1) then
				do;
					if (CLUSTER_id_data eq .) then
						do;
							CLUSTER_id_data = VAR_CLUSTER_ID;
						end;
				end;

			if (CLUSTER_id_data ne var_CLUSTER_id) then
				do;
					CLUSTER_id_key = var_CLUSTER_id;
				end;

			if (CLUSTER_id_key ne .) & (CLUSTER_id_data ne .) & (CLUSTER_id_key ne CLUSTER_id_data) then
				do;
					output;
				end;

			if last.CLUSTER_ID_OLD then
				do;
					CLUSTER_id_data =.;
				end; 
		end;
run;

%rcSet(&syserr); 

/* This step will rectify instances of one old CLUSTER_id having multiple new CLUSTER_id linkages */

data NEW_CLUSTER_TEMP (COMPRESS=YES);
	drop CLUSTER_id_new CLUSTER_id_key CLUSTER_id_data s_CLUSTER_id s_var_CLUSTER_id rc rc2 rc3 rc4;

 	attrib CLUSTER_id_new length=8. format=Best12.;
 	attrib CLUSTER_id_key length=8. format=Best12.;
 	attrib CLUSTER_id_data length=8. format=Best12.;
	attrib s_CLUSTER_id length=8. format=Best12.;
	attrib s_var_CLUSTER_id length=8. format=Best12.;

	if _N_ = 1 then 
	do;
		declare hash myhash(hashexp: 13, dataset: "CLUSTER_DMC_FINAL_V");
		myhash.defineKey('CLUSTER_id_key');
		myhash.defineData('CLUSTER_id_key','CLUSTER_id_data');
		myhash.defineDone();
		call missing(CLUSTER_id_key, CLUSTER_id_data);

		declare hash s_hash(hashexp: 13);
		s_hash.defineKey("s_CLUSTER_id");
		s_hash.defineData("s_var_CLUSTER_id");
		s_hash.defineDone();
		call missing(s_CLUSTER_id, s_var_CLUSTER_id);		
	end;
	set &ipdsn;
	CLUSTER_id_key = var_CLUSTER_id;
	rc = myhash.find();
	if (rc = 0) then
		do;
			CLUSTER_id_new = CLUSTER_id_data;
			s_CLUSTER_id = CLUSTER_id_data;

			rc2 = s_hash.find();
			if (rc2 = 0) then
				do;
					CLUSTER_id_new = s_var_CLUSTER_id;
				end;
			else
				do;
					CLUSTER_id_key = CLUSTER_id_data;
					s_var_CLUSTER_id = CLUSTER_id_data;
					rc3 = myhash.find();
					if (rc3 = 0) then
						do;
							CLUSTER_id_new = CLUSTER_id_data; 
							s_var_CLUSTER_id = CLUSTER_id_data;
							if (CLUSTER_id_data = var_CLUSTER_id) then
								do;
									CLUSTER_id_new = var_CLUSTER_id;
								end;
						end;
					s_CLUSTER_id = var_CLUSTER_id;
					rc4 = s_hash.add();
				end;
		end;
	if CLUSTER_id_new ne . then
		do;
			var_CLUSTER_id = CLUSTER_id_new;
		end;
run;

%rcSet(&syserr); 

/* Delete the temporary data step view */

proc datasets lib=work nolist memtype=(Data View);
	delete CLUSTER_DMC_FINAL_V;
quit;

%rcSet(&syserr); 

/* sort on CLUSTER_ID_OLD to resolve any de-linking of clusters */

proc sort data=NEW_CLUSTER_TEMP;
	by CLUSTER_ID_OLD VAR_CLUSTER_ID;
run;

%rcSet(&syserr); 

/* create a view to act as a input for hash */

data CLUSTER_DMC_FINAL_V / view=CLUSTER_DMC_FINAL_V ;
 	attrib CLUSTER_id_key length=8. format=Best12.;
 	attrib CLUSTER_id_data length=8. format=Best12.;
	keep CLUSTER_id_key CLUSTER_id_data;
	retain CLUSTER_id_data;
