/* This step will rectify instances of one old CLUSTER_id having multiple new CLUSTER_id linkages */

data NEW_CLUSTER_TEMP_2 (COMPRESS=YES);
	drop CLUSTER_id_new CLUSTER_id_key CLUSTER_id_data s_CLUSTER_id s_var_CLUSTER_id rc rc2 rc3 rc4;

