%macro actualMac;

/*Looping through announcements, 1 by 1, and identifying the time windows for analysis*/

%let i = 1;

	proc sql noprint;
		select count(*) 
		into :maxrow
		from Annment&date;
	quit;
/*%put &maxrow;*/

%do %while(&i <= &maxrow);

	data _null_;
		set Annment&date;
		call symputx('scripcd', scripcd);
		call symputx('anndate1', put(anndate , Date9. ));
		call symputx('rownum', rownum);
		call symputx("body", body);
		call symputx("FavUnfav", fav_unfav);
		where rownum = &i;
	run;

	%let i = %eval(&i + 1);
	%let anndate  = "&anndate1"d ;

	proc sql noprint;
		create table meantab as 
		select 
		no_of_shares_traded as Totbuyqty, 
		datepart(trade_date) as trandate format date9.,
		&anndate as AnnDate,
		INTNX('Day', &anndate, -365) as Lastyear format date9.
		from tt.SCRIP_SUMMERY
		where scrip_code = &Scripcd and calculated trandate between calculated Lastyear and &anndate
		ORDER BY CALCULATED trandate DESC;
	quit;

	PROC MEANS DATA=meantab noprint;
		VAR Totbuyqty;
		OUTPUT OUT=ButMEANS&Scripcd
       	MEAN=BuyMEAN median=buyMedian;
    RUN;

	proc sql noprint;
	select BuyMEAN into :BuyMean from ButMEANS&Scripcd;
	quit;

	data Sus&Scripcd;
		set tt.SCRIP_SUMMERY;
		format date anndate date9. varRatio 10.2;
		drop SCRIP_GROUP_CODE PREVIOUS_CLOSING_DATE PREVIOUS_CLOSING_RATE PERCENT_CHANGE net_turnover
			CLOSING_INDICATOR LAST_TRADED_PRICE LISTED_STATUS trade_date NUMBER_OF_SHARES_FOR_INDEX
			high_rate low_rate open_rate;
		date = datepart(trade_date);
		Anndate = &anndate;
		BuyMean = &BuyMean;
		if BuyMean = 0 then varRatio = 0;
		else varRatio = no_of_shares_traded/BuyMean;
		where scrip_code = &Scripcd
		and datepart(trade_date) >= INTNX('day', &AnnDate, -60) and datepart(trade_date) < &AnnDate;
	run;

	proc sql noprint;
	create table Date&Scripcd.1 as
	select distinct
	scrip_code as scripcd,
	Anndate format date9.,
	date as EPFirst1 format date9.,
	INTNX('day', anndate, 7) as EPLast1 format date9.,
	INTNX('day', date, -60) as APFirst1 format date9.,
	INTNX('day', anndate, 14) as APLast1 format date9.,
	varratio
	from (select min(date) as date, min(anndate) as anndate, min(scrip_code) as scrip_code, max(varratio) as varratio
			from (select * from Sus&Scripcd where varratio >= 10));	
quit;

proc sql;
create table testmax as
	select distinct
	scrip_code as scripcd,
	max(close_rate) as MaxCloseRate,
	INTNX('day', &anndate, 14) as MaxDate format = date9.
	from tt.SCRIP_SUMMERY
	where datepart(trade_date) >= &anndate and datepart(trade_date) <=  INTNX('day', &anndate, 14)
	and scrip_code = &scripcd and close_rate > 0;
quit;

proc sql;
create table testmin as
	select distinct
	scrip_code as scripcd,
	close_rate as MinCloseRate,
	max(datepart(trade_date)) as Mindate format = date9.
	from tt.SCRIP_SUMMERY 
	where close_rate = (
	select
	min(close_rate)
	from tt.SCRIP_SUMMERY
	where datepart(trade_date) >= INTNX('day', &anndate, -60) and datepart(trade_date) <  &anndate
	and scrip_code = &scripcd and close_rate > 0) and scrip_code = &scripcd
	group by close_rate, scripcd;
quit;

	proc sql;
	create table Date&Scripcd.2 as
	select distinct
	a.scripcd,
	a.fld_longname as CompanyName,
	a.companyid,
	&anndate as anndate,
	b.EPFirst1,
	b.EPLast1,
	b.APFirst1,
	b.APlast1,
	b.varratio,
	c.maxCloserate,
	c.Maxdate,
	d.mincloserate,
	d.mindate,
	(c.maxcloserate - d.mincloserate)/d.mincloserate * 100 as PriceVariation  format = comma10.2,
	a.fav_unfav,
	case
		when varratio >= 10 and sum(calculated PriceVariation, 0) >= 0 then 'a. Volume Variation'
		when  varratio < 10 and calculated PriceVariation > 20 then 'b. Price Variation'
		else 'c. Not Select'
	end as Selection
	from Annment&date a,  Date&Scripcd.1 b, testmax c, testmin d
	where a.scripcd = &scripcd;
	quit; 

	Proc sql;
		create table Date&Scripcd.t as
		select distinct
		scripcd,
		companyName,
		companyid, 
		anndate format = date9.,
		case
			when varratio > 0 and sum(PriceVariation, 0) >= 0 then EPFirst1
			when PriceVariation > 20 then Mindate
			else .
		end as EPFirst format date9.,
		case
			when calculated EPFirst > 0 then INTNX('day', anndate, 7)
			else .
		end as EPLast format = date9.,
		case
			when calculated EPFirst > 0 then INTNX('day', calculated EPFIrst, -60)
			else .
		end as APFirst format = date9.,
		case
			when calculated EPFirst > 0 then INTNX('day', anndate, 14)
			else .
		end as APLast format = date9.,
		varratio,
		MaxcloseRate as MaxRate,
		MinCloseRate as Minrate,
		PriceVariation,
		Selection,
		Fav_Unfav
		from Date&Scripcd.2
	quit;

	data _null_;
	set Date&Scripcd.t;
	call symputx('APFirstV', APFirst);
	run;

	%IF &APFirstV = . %THEN %do;
		%let APFirstF = "31dec2099"d;
	%end; 
	%ELSE %do; %let APFirstF = &APFirstV; %end;


	%put &APFirstV &APFirstF &todate &scripcd;

	proc sql;
	create table ExFil&scripcd as
	select
	scrip_code as scripcd,
	avg(no_of_shares_traded) as AvgVol,
	max(close_rate) as RateFilter
	from tt.scrip_summery
	where datepart(trade_date) >= &APfirstF and datepart(trade_date) <= &todate and scrip_code = &scripcd
	group by scrip_code;


	create table Date&Scripcd as
	select
	a.*,
	sum(b.AvgVol, 0) as AvgVol,
	sum(b.RateFilter, 0) as RateFilter
	from Date&Scripcd.t a left outer join ExFil&scripcd b
	on a.scripcd = b.scripcd;
	quit;

	proc sql;
		insert into Datetab&date.1
   		select * from Date&Scripcd;

		create table  Datetab&date as
		select distinct 
		scripcd,
		CompanyName,
		companyid,
		Anndate format = date9.,
		EPFirst format = date9.,
		EPLast format = date9.,
		APFirst format = date9.,
		APLast format = date9.,
		Avgvol format = 10.2,
		RateFilter format = 10.2,
		varratio format = 10.2,
		MaxRate  format = 10.2,
		minrate format = 10.2,
		PriceVariation format = 10.2,
		Selection,
		Fav_Unfav,
		case
			when (Avgvol >= 10000) or (RateFilter >= 10) then 1 
			else 0
		end as AnalyseFlag
		from Datetab&date.1
		order by AnalyseFlag desc, selection asc, fav_unfav desc;
	quit;


	proc datasets library = work nolist;
    delete BUTMEANS&scripcd SUS&scripcd DATE&scripcd DATE&scripcd.1 DATE&scripcd.2;
	quit;

%end;
%mend actualMac;


