/*Report creation macro*/
%macro report;

data InsiderReportTemp;
set ABTTEMPV1i;
if trandate < &anndate and trandate >= &APFirst then PEBuy = TotBuyQty;
else PEBUY = 0;
if trandate < &anndate and trandate >= &APFirst then PESell = TotSellQty;
else PESell = 0;
if trandate >= &anndate and trandate <= &APLast then PoBuy = TotBuyQty;
else PoBUY = 0;
if trandate >= &anndate and trandate <= &APLast then PoSell = TotSellQty;
else PoSell = 0;

if trandate < &anndate and trandate >= &APFirst then PEBuyVal = TotBuyVal/100;
else PEBUYVal= 0;
if trandate < &anndate and trandate >= &APFirst then PESellVal = TotSellVal/100;
else PESellVal = 0;
if trandate >= &anndate and trandate <= &APLast then PoBuyVal = TotBuyVal/100;
else PoBUYVal = 0;
if trandate >= &anndate and trandate <= &APLast then PoSellVal = TotSellVal/100;
else PoSellVal = 0;
run;

