/*Summary Table*/
proc sql;
create table IRSum&scripcd as 
select distinct
b.rownum,
a.panno,
/*max(b.clientname) as clientname,*/
/*max(b.phone) as phone,*/
/*max(b.address) as address,*/
a.scripcd,
&anndate as AnnDate  format date9. ,
&APFirst as FromDate format date9.,
&APLast as ToDate format date9.,

sum(PEBUY) as PreGPQTY format comma15.0,
case
	when calculated PreGPQTY <> 0 then (sum(PEBUYVal)/calculated PreGPQTY) 
	else 0
end as PreBuyRate  format comma15.2,
sum(PESell) as PreGSQty format comma15.0,
case
	when Calculated PreGSQty <> 0 then (sum(PESellVal)/Calculated PreGSQty)
	else 0
end as PreSellRate format comma15.2,
calculated PreGPQTY - calculated PreGSQty as PreNPQTY format comma15.0,
calculated PreGSQty - calculated PreGPQTY as PreNSQty format comma15.0,

sum(PoBUY) as PostGPQTY format comma15.0,
case
	when calculated PostGPQTY <> 0 then (sum(PoBUYVal)/calculated PostGPQTY) 
	else 0
end as PostBuyRate format comma15.2,
sum(PoSell) as PostGSQty format comma15.0,
case
	when Calculated PostGSQty <> 0 then (sum(PoSellVal)/Calculated PostGSQty)  
	else 0
end as PostSellRate format comma15.2,
calculated PostGPQTY - calculated PostGSQty as PostNPQTY format comma15.0,
calculated PostGSQty - calculated PostGPQTY as PostNSQty format comma15.0,

(calculated PreGPQTY + calculated PostGPQTY) as GPQTY format comma15.0,
case
	when calculated GPQTY <> 0 then ((sum(PEBUYVal)+ sum(PoBUYVal))/calculated GPQTY) 
	else 0
end as NetBuyRate format comma15.2,
(calculated PreGSQTY + calculated PostGSQTY) as GSQty format comma15.0,
case
	when calculated GSQty <> 0 then ((sum(PESellVal) + sum(PoSellVal))/calculated GSQty) 
	else 0
end as NetSellRate format comma15.2,
(calculated PreGSQTY + calculated PostGSQTY) - (calculated PreGPQTY + calculated PostGPQTY) as NetGSQty format comma15.0,
(calculated PreGPQTY + calculated PostGPQTY) - (calculated PreGSQTY + calculated PostGSQTY) as NetGPQTY format comma15.0,

/*&Close_rateBSE/100 as CloseRate format comma15.2,*/
/*&Open_rateBSE/100 as OpenRate format comma15.2,*/
/*case*/
/*	when calculated PostGSQty < calculated PreGPQTY then calculated PostGSQty * (calculated PostSellRate - calculated PreBuyRate)*/
/*	when calculated PreGPQTY <= calculated PostGSQty then calculated PreGPQTY * (calculated PostSellRate - calculated PreBuyRate)*/
/*	else 0*/
/*end as Profit format comma15.0,*/
min(max(0, calculated PreNPQTY), max(0, calculated PostNSQty)) * (calculated PostSellRate - calculated PreBuyRate) as Profit format comma15.0,
min(max(0, calculated PreNPQTY), max(0, calculated PostNSQty)) * (calculated PostSellRate - calculated PreBuyRate) as Profit format comma15.0,
/*&Close_rate/100 as CloseRate,*/
&Close_rateBSE/100 as CloseRate,
case
	when &favtag = "F" then
(max(0, calculated PostNSQty) * calculated PostSellRate) - (max(0, calculated PreNPQTY) * calculated PreBuyRate) + (max(0, calculated PreNPQTY) * &Close_rateBSE/100) 
	when &favTag = "U" then b.notprof/100
	else 0
end as NotionalProfit format comma15.0
/*case*/
/*	when calculated PostGSQty >= calculated PreGPQTY then (calculated PostGSQty * calculated PostSellRate) - (calculated PreGPQTY * calculated PreBuyRate)*/
/*	when calculated PostGSQty < calculated PreGPQTY then (calculated PreGPQTY * (calculated PostSellRate - calculated PreBuyRate)) + ((calculated PreGPQTY - calculated PostGSQty) * &Close_rateBSE/100) */
/*	else 0*/
/*end as NotionalProfit format comma15.0*/
/*b.notprof as NotionalProfit format comma15.0*/
/*sum(PoSellVal) - sum(PEBUYVal) + (max(0, (sum(PEBUY) - sum(PoSell))) * &Close_rateBSE)/100 as NotionalProfit format comma15.0*/
from InsiderReportTemp a right outer join Insiders&scripcd b on a.panno = b.pannoap
/*where a.panno in (select pannoap from Insiders&scripcd)*/
group by a.panno, b.rownum
order by b.rownum asc;
quit;

