/*Transaction detail table*/
proc sql;
create table IRTran&scripcd as 
select distinct
a.panno,
a.scripcd,
a.trandate format date9.,
a.totbuyqty as GPQty format comma15.0,
(a.TotBuyval/a.totbuyqty)/100  as buy_rate format comma15.2,
(a.totbuyqty/a.no_of_shares_traded) * 100 as GPToScripVol format comma15.5,
a.totsellqty as GSQTY format comma15.0, 
(a.totsellval/a.totsellqty)/100  as sell_rate format comma15.2,
(a.totsellqty/a.no_of_shares_traded) * 100 as GSToScripVol format comma15.5,
(a.totbuyqty - a.totsellqty) as NPQty format comma15.0,
a.no_of_shares_traded as ScripVolume format comma15.0,
b.NotionalProfit
from ABTTEMPV1i a left outer join IRSum&scripcd b
on a.panno = b.panno
where a.panno in (select pannoap from Insiders&scripcd)
order by b.rownum asc, a.panno, a.trandate;
quit;

/*Across the market bahviour table*/
proc sql;
create table IRMart&scripcd.1 as 
select distinct
panno,
scripcd,
sum(totbuyqty) as GPQty format comma15.0,
(sum(TotBuyval)/sum(totbuyqty))/100 as buy_rate format comma15.2,
sum(totsellqty) as GSQTY format comma15.0, 
(sum(totsellval)/sum(totsellqty))/100  as sell_rate format comma15.2,
(sum(totbuyqty) - sum(totsellqty)) as NPQty format comma15.0,
(sum(totbuyqty) + sum(totsellqty)) as TotQty,
sum(no_of_shares_traded) as ScripVolume format comma15.0
from ABT&scripcd.Raw
where panno in (select pannoap from Insiders&scripcd)
group by  panno, scripcd
order by panno, scripcd;
quit;

proc sql;
create table IRMart&scripcd as 
select
a.PANNO,
a.SCRIPCD,
a.GPQty,
a.buy_rate,
a.GPQty/a.ScripVolume * 100 as GPToScripVol format comma15.2,
a.GSQTY,
a.sell_rate,
a.GSQty/a.ScripVolume * 100 as GSToScripVol format comma15.2,
a.NPQty,
a.TotQty/b.GrandTot * 100 as Exposureratio format comma15.2,
a.ScripVolume 
from IRMart&scripcd.1 a left outer join 
(select panno, sum(TotQty) as GrandTot, 
sum(GPQty) as GrandBuy,  
sum(GSQty) as GrandSell
from IRMart&scripcd.1 group by panno) b
on a.panno = b.panno
order by a.panno, a.scripcd;
quit;

proc sql;
create table IRSum&scripcd.v1 as
select a.*,
b.Exposureratio
from IRSum&scripcd a left outer join IRMart&scripcd b 
on a.panno = b.panno and a.scripcd = b.scripcd
order by a.rownum asc;
quit;

proc sql;
create table UCCTemp as
select
