proc sql;
	create table ABT&scripcd.Raw as
	select distinct
	max(a.panno) as pannotemp,
	c.panno,
	a.Scripcd,
	datepart(trandate) as trandate format = date9.,
	sum(TotBuyQty) as TotBuyQty,
