/*raw data manipulation to create ABT*/
proc sql;
	create table ABTTEMPV1i as
	select * from ABT&scripcd.Raw
	where scripcd = &scripcd
	order by panno, trandate;
quit;


data ABTTEMPV1i;
	set ABTTEMPV1i;
	by panno;
	format POST_ADJST_LONG_QUANTITY best8. POST_ADJST_SHORT_QUANTITY best8.;
	retain POST_ADJST_LONG_QUANTITY 0 POST_ADJST_SHORT_QUANTITY 0;
	if first.panno then do;
	POST_ADJST_LONG_QUANTITY =0 ;
	POST_ADJST_SHORT_QUANTITY =0;
	end;
	if sum(TotBuyQty, 0) > sum(TotSellQty, 0) then DayStatus = "B";
	else if sum(TotBuyQty, 0) < sum(TotSellQty, 0) then DayStatus = "S";
	else if sum(TotBuyQty, 0) = sum(TotSellQty, 0) then DayStatus = "N";
	if TotBuyQty > TotSellQty then do;
	POST_ADJST_LONG_QUANTITY = max(sum(POST_ADJST_LONG_QUANTITY, TotBuyQty, - TotSellQty, - POST_ADJST_SHORT_QUANTITY),0);
	POST_ADJST_short_QUANTITY = max(sum(POST_ADJST_short_QUANTITY, -TotBuyQty, TotSellQty),0);
	end;
	else do;
	POST_ADJST_SHORT_QUANTITY = max(sum(POST_ADJST_SHORT_QUANTITY, TotSellQty, - TotBuyQty, - POST_ADJST_long_QUANTITY),0);
	POST_ADJST_LONG_QUANTITY = max(sum(POST_ADJST_LONG_QUANTITY, - TotSellQty, TotBuyQty),0);
	end;
	if POST_ADJST_LONG_QUANTITY = POST_ADJST_SHORT_QUANTITY then type_intrst='NT';
	else if POST_ADJST_LONG_QUANTITY > POST_ADJST_SHORT_QUANTITY then type_intrst='LG';
	else if POST_ADJST_LONG_QUANTITY < POST_ADJST_SHORT_QUANTITY then type_intrst='ST';
	open_intrst= ABS(POST_ADJST_LONG_QUANTITY - POST_ADJST_SHORT_QUANTITY);
	if sum(POST_ADJST_LONG_QUANTITY ,- POST_ADJST_SHORT_QUANTITY) > 0 and TotBuyQty ne 0 then
	hold_INTRST = (sum(POST_ADJST_LONG_QUANTITY, -POST_ADJST_SHORT_QUANTITY)/no_of_shares_traded);
	else HOLD_INTRST=0;
	if sum(TotBuyQty,-TotSellQty)>0 and TotBuyQty ne 0 then
	days_hold = ((sum(TotBuyQty,-TotSellQty)) / no_of_shares_traded);
	ELSE days_hold = 0;
	OpenIntrstRat = open_intrst/no_of_shares_traded;
run;

proc sort data = ABTTEMPV1i;
	by panno trandate;
run;

data test1;
	set ABTTEMPV1i (KEEP= type_intrst trandate);
	rename type_intrst = nxt_type_intrst ;
