%macro ABTTemp(First, Last, Period, s);

data ABTTemp&Period.v1;
	set cmcase (where = (trandate BETWEEN &first AND &last));
	BY panno;
	retain fdate 0;
	format dur best8.;
	format tdate fdate date9.;
	if first.panno or type_intrst ne lag1(type_intrst) then count1 = 0;
	else count1 + 1;
/*	EPLast = &EPLast;*/
	if last.panno then tDate = &last;
	else tDate = trandate;
	if first.panno or type_intrst ne lag1(type_intrst) then fdate = trandate;
	else fdate = fdate;
	if last.panno or type_intrst ne nxt_type_intrst then dur = (tdate - fdate);
	if last.panno or type_intrst ne nxt_type_intrst then count = count1;
run;

proc sql;

	create table ABTTemp&Period.v2 as
	select
	panno as panno1&s,
	type_intrst as type_intrst&s,
	sum(dur) as dur,
	count(dur) as run,
	(&Last - &first) as PLenght,
	(calculated dur/ (&Last - &first)) as hold_dur,
	((calculated dur)/(calculated run)) as avg_dur
	from ABTTemp&Period.v1
	group by panno, type_intrst;

quit;

data LGrun&period (keep = panno1&s LG:)
	STrun&period (keep = panno1&s  ST:)
	NTrun&period (keep = panno1&s  NT:);
set ABTTemp&Period.v2;

if type_intrst&s= 'LG' then do ;
  LG_dur&s = dur;
  LG_avg_dur&s=avg_dur;
  LG_run&s=run;
  LG_hold&s=hold_dur;
  output LGrun&period;
end;
else if type_intrst&s= 'NT' then do ;
  NT_dur&s = dur;
  NT_avg_dur&s=avg_dur;
  NT_run&s=run;
  NT_hold&s=hold_dur;
  output NTrun&period;
 end;
 else if type_intrst&s= 'ST' then do ;
  ST_dur&s = dur;
  ST_avg_dur&s=avg_dur;
  ST_run&s=run;
  ST_hold&s=hold_dur;
  output STrun&period;
 end;
run;

proc sql;
	create table ABTTemp&Period as 
	select
	panno as panno&s,
	sum(TotBuyQty) as QtyB&s,
	sum(TotSellQty) as QtyS&s,
	sum(TotBuyVal) as ValB&s,
	sum(TotSellVal) as ValS&s,
	sum(TotBuyVal ne 0) as FreqB&s,
	sum(TotSellVal ne 0) as FreqS&s,
	(calculated QtyB&s + calculated QtyS&s) as QTY&s,
	(calculated QtyB&s - calculated QtyS&s) as NetBuy&s,
	(calculated ValB&s + calculated VAlS&s) as Val&s,
	(calculated ValB&s - calculated VAlS&s) as NetVal&s,
	(calculated FreqB&s + calculated FreqS&s) as Freq&s,
	sum(type_intrst = 'NT') as FreqNT&s,
	sum(type_intrst = 'LG') as FreqLG&s,
	sum(type_intrst = 'ST') as FreqST&s,
	sum(DayStatus = 'B') as FreqDB&s,
	sum(DayStatus = 'S') as FreqDS&s,
	sum(DayStatus = 'N') as FreqDN&s,
	count(panno) as FreqOpen&s,
	(calculated FreqLG&s)/(calculated FreqOpen&s) as LGOpenRatio&s,
	1000000 * avg(hold_intrst) as AvgHoldIntrst&s format = 10.4,
	1000000 * max(hold_intrst) as MaxHoldIntsrt&s format = 10.4,
	1000000 * min(hold_intrst) as MinHoldIntrst&s format = 10.4,
	1000000 * avg(OpenIntrstRat) as AvgOpenIntRat&s format = 10.4,
	1000000 * max(OpenIntrstRat) as MaxOpenIntRat&s format = 10.4,
	1000000 * min(OpenIntrstRat) as MinOpenIntRat&s format = 10.4,
	1000000 * avg(days_hold) as AvgDaysHold&s format = 10.4,
	1000000 * max(Days_hold) as MaxDaysHold&s format = 10.4,
	1000000 * min(days_hold) as minDaysHold&s format = 10.4
	from ABTTemp&Period.v1
	group by panno; 

quit;

