proc sql;
	create table ABTTempv5 as 
	select distinct
	a.*, 
	b.totsellqty as lSellqty,
	b.totsellval as lSellVol,
	b.trandate as lselldt,
	(b.trandate - &APFirst) as dslsell,
	(calculated dslsell)/Preevnt as ExitSell,
	c.totbuyqty as lBuyqty,
	c.totbuyval as lBuyVol,
	c.trandate as lBuydt,
	(c.trandate - &APFirst) as dslbuy,
	(calculated dslbuy)/Preevnt as ExitBuy,
	netbuyAP as OverAllNetbuy,
	case when qty = . then 0 else Qty/QtyAp end as TradeRatio,
	case when Freq = . then 0 else Freq/FreqAP end as FreqRatio
	from ((ABTTempv4 a left outer join Periodsl b on a.pannoAP = b.panno)
						left outer join Periodbl c on a.pannoAP = c.panno);


	create table OtherScrip&scripcd as
	select 
	panno,
	sum(TotBuyQty) as oth_buy,
	sum(TotSellQty) as oth_sale,
	(calculated oth_buy - calculated oth_sale) as oth_net_buy,
	count(distinct scripcd) as oth_stock_n,
	count(panno) as oth_freq,
	sum(TotBuyQty > 0) as oth_freq_b,
	sum(TotSellQty > 0) as oth_freq_s,
	sum(TotbuyQty = TotSellqty) as oth_freq_n
	from ABT&scripcd.Raw
	where scripcd ne &scripcd
	group by panno
	order by panno;


	select 
	max(close_rate) * 100,
	min(close_rate) * 100 
	into :close_rate, :open_rate
	from ABT&scripcd.Raw
	WHERE scripcd = &Scripcd;

	select 
	max(close_rate) * 100
	into :Close_rateBSE
	from ABTTEMPV1i
	where trandate >= &todate;

	select 
	min(close_rate) * 100
	into :open_rateBSE
	from ABTTEMPV1i
	where trandate = (Select min(trandate) from ABTTEMPV1i); 


	/**%put &close_rate &open_rate;*/
	create table abt_&scripcd.v1 as
	select distinct
	a.*,
	b.*,
	&scripcD as Scripcd,
	&AnnDate as Anndate format = date9., 
	/*--proportion of buy after event;*/
	case
		when sum(0, QtySPO, QTYBPO) = 0 then 0
		else sum(QtyBPO, 0)/sum(0, QtySPO, QTYBPO) 
	end as PropBuyPO,
	/*--proportion of sell before event;*/
	case
		when sum(0, QTYSPO, QTYSPE) = 0 then 0
		else QTYSPE/sum(0, QTYSPO, QTYSPE)
	end as PropSell,
	/*--proportion of buy before event;*/
	case
		when sum(0, QtySPE, QTYBPE) = 0 then 0
		else sum(QtyBPE, 0)/sum(0, QtySPE, QTYBPE) 
	end as PropBuyPE,
	/*--proportion of ScripVal bought compared to total invested;*/
	case
		when oth_buy in (0,.) then ( 1 - (1 /(1 + sum(0,ValBAP)) ))*100
		ELSE ( 1 - (1 /(1 + sum(0,ValBAP)/oth_buy) ))*100
	end as hold_buy_all,

	/**proportion of ScripVal bought compared to total sold;*/
	case
		when oth_sale in (0,.) then ( 1 - (1 /(1 + sum(0,ValBAP)) ))
		ELSE ( 1 - (1 /(1 + sum(0,ValBAP)/oth_sale) ))*100
	end as hold_buy_sale_all,

	/**proportion of ScripVal sold compared to total sold;*/
	case
		when OTH_SALE in (0,.) then ( 1 - (1 /(1 + sum(0,VAlSAP)) ))*100
		ELSE ( 1 - (1 /(1 + sum(0,ValSap)/OTH_SALE) ))*100
	END AS hold_SALE_all,
	/**ratio between frequncy of nuetral vs long or short max;*/
	CASE
		WHEN max(FreqSTAP, FreqLGAP) = 0 then 0
		else freqntAP/max(FreqSTAP, FreqLGAP)
	END AS freq_n_ratio,
	/*Profit as is*/
	case
		when NetBuyAP > 0 then ValSAP - ValBAP
		when NetBuyAP < 0 then ValSAP + (NetBuyAP * &open_rate) - ValBAP
		else ValSAP - ValBAP
	end as profit
	from ABTTempv5 a left outer join OtherScrip&scripcd b
	on a.pannoAP = b.panno;
	*where a.pannoAp is not null;
quit;

proc sql;
	create table abt_&scripcd as
	select
	*,
	case
		when QTYSap = 0 and NetBuyAP > 0 then (&close_rate-(VALBAP/QTYBAP))*NetBuyAP
		when QTYSap ne 0 and NetBuyAP > 0 THEN sum(((&close_rate-(VAlBAP/QTYBAP))*NetBuyAP),
			(((ValSAP/(QTYSAP+0.0000001))-(ValBAP/QTYBAP))*QTYSap))
		when QTYBap = 0 and NetBuyAP < 0 then &close_rate * (-NetBuyAP)
		when NetBuyAP < 0 and QTYBAP ne 0 then SUM(((&close_rate-(ValBAP/(QTYBAP+0.00000001)))*(-NetBuyAP)),
			(((ValSAP/QTYSAP)-(ValBAP/(QTYBAP+0.00000001)))*QTYBap))
		else ValSAP - ValBAP
	end as Notprof format 10.0,
	case 
		when NetBuyAP > 0 then  sum(ValSAP, - ValBAP) + NetBuyAp * (&Close_rateBSE)
		when sum(ValBAP, 0) = 0 then .
		else sum(ValSAP, - ValBAP)
	end as  NotprofBSe format 10.0,
	QtyB = sum(0, QtyB)
	from abt_&scripcd.v1;
quit;



