proc sql;
	create table TestVal as
	select
		cat(a.PannoAP, a.scripcd) as IDTot,
		a.scripcd,
		a.pannoap,
		a.FreqLGAP,
		a.LGOpenRatio format 10.2,
		a.freqAP,
		a.r_freqAP,
		Log(a.MaxDaysHold + 1) as LogMaxDayHold  format 10.2,
		Log(a.MaxopenintRat + 1) as LogMaxopenintRat format 10.2,
		Log(a.MinHoldIntrst + 1) as LogMinHoldInst format 10.2,
		Log(a.MinHoldIntrstPE + 1) as LogMinHoldInstPE format 10.2,
		log(a.mindaysholdAP + 1) as logmindayholdAP format 10.2,
		a.r_Freq,
		a.r_FreqLGAP,
		a.r_FreqNT,
		a.r_FreqSTPE,
		a.r_LGOpenRatioAP,
		a.r_NetValPE,
		a.r_NotProf,
		a.r_ST_hold,
		a.r_dsfbuy,
		a.r_profit,
		a.freqNTAP,
		a.QtySPO format comma10.0,
		a.QtyBPO format comma10.0,
		a.PropBuyPE format 10.2,
		a.PropBuyPo format 10.2,
		a.PropSell format 10.2,
		a.anndate, 
		a.fbuydt, 
		a.FreqBAP, 
		a.FreqSAP, 
		a.fselldt,
		a.freqdnap,
		a.notprof format comma10.0,
		a.NotprofBSe format comma10.0,
/*		***  Effect: FreqLGAP ;*/
		-0.12834041446128 * a.FreqLGAP as eFreqLGAP,
/*		***  Effect: LGOpenRatio ;*/
		9.35171677320432 * a.LGOpenRatio as eLGOpenRatio,
/*		***  Effect: LogMaxDayHold ;*/
		0.50721933937774 * calculated LogMaxDayHold as eLogMaxDayHold,
/*		***  Effect: LogMaxopenintRat ;*/
		0.50647075156291 * calculated LogMaxopenintRat as eLogMaxopenintRat,
/*		***  Effect: LogMinHoldInst ;*/
		-0.41578799362144 * calculated LogMinHoldInst as eLogMinHoldInst,
/*		***  Effect: LogMinHoldInstPE ;*/
		0.33056824248213 * calculated LogMinHoldInstPE as eLogMinHoldInstPE,
/*		***  Effect: logmindayholdAP ;*/
		0.30009596072814 * calculated logmindayholdAP as elogmindayholdAP,
/*		***  Effect: r_Freq ;*/
		0.04775158362027 * a.r_Freq as er_Freq,
/*		***  Effect: r_FreqLGAP ;*/
		-0.07847124677054 * a.r_FreqLGAP as er_FreqLGAP,
/*		***  Effect: r_FreqNT ;*/
		-0.04362903766159 * a.r_FreqNT as er_FreqNT,
/*		***  Effect: r_FreqSTPE ;*/
		0.06488499557156 * a.r_FreqSTPE as er_FreqSTPE,
/*		***  Effect: r_LGOpenRatioAP ;*/
		-0.15819735875969 * a.r_LGOpenRatioAP as er_LGOpenRatioAP,
/*		***  Effect: r_NetValPE ;*/
		0.02325929070143 * a.r_NetValPE as er_NetValPE,
/*		***  Effect: r_NotProf ;*/
		1.16144265698088 * a.r_NotProf as er_NotProf,
/*		***  Effect: r_ST_hold ;*/
		-0.07294345844204 * a.r_ST_hold as er_ST_hold,
/*		***  Effect: r_dsfbuy ;*/
		0.03202068081495 * a.r_dsfbuy as er_dsfbuy,
/*		*** Intercept;*/
		-118.931680520813 as eIntercept
		from Model&scripcd a
		where a.r_NotProf >= 90.0 and r_ValAP >= 50.0 and a.PannoAP is not null
		order by a.scripcd;
quit;

proc sql;
	create table test as 
	select 
	*,
	case
/*		when freqdnap >= 1 then "day trader"*/
		when FreqSAP = 0 and fbuydt >= anndate then "only buy & after event"
		when FreqBAP = 0 and PropSell > 0.80 then "No buy and sell happen before event date"
		when FreqBAP = 0 and qtySPO = 0 then "only sell & before event"
		when PropBuyPO >= 0.60 then "mostly buy after event"
		when qtyBpo = 0 and QtySPO = 0 and PropBuyPE <= 0.75 then "mostly sell before event"
		else "potential insider"
	end as Waterfall,
	((-0.12834041446128 * FreqLGAP) +
		(9.35171677320432 * LGOpenRatio) +
		(0.50721933937774 * LogMaxDayHold) +
		(0.50647075156291 * LogMaxopenintRat) + 
		(-0.41578799362144 * LogMinHoldInst) +
		(0.33056824248213 * LogMinHoldInstPE) +
		(0.30009596072814 * logmindayholdAP) +
		(0.04775158362027 * r_Freq) +
		(-0.07847124677054 * r_FreqLGAP) +
		(-0.04362903766159 * r_FreqNT) +
		(0.06488499557156 * r_FreqSTPE) +
		(-0.15819735875969 * r_LGOpenRatioAP) +
		(0.02325929070143 * r_NetValPE) +
		(1.16144265698088 * r_NotProf) + 
		(-0.07294345844204 * r_ST_hold) +
		(0.03202068081495 * r_dsfbuy) +
		(-118.931680520813)) as x,
	exp(calculated x) / (1 + exp(calculated x)) as px,
	case
		when calculated px >= 3/45 and calculated waterfall = "potential insider" then 1
		else 0
	END AS Decision,
	case
		when calculated Decision = 0 then "Not Insider"
		when calculated Decision = 1 and PropBuyPE < 0.10 and PropBuyPO < 0.60 then "insider - possibly(sell)"
		when calculated Decision = 1 and qtyBpo = 0 and QtySPO = 0 and PropBuyPE > 0.75 then "insider - possibly(buy)"
		else "Insider proper"
	end as InsiderFine
	from TestVal
order by scripcd, Decision desc, px desc;
quit;

proc sql noprint;
create table intermed as
select * 
from test
where waterfall = "potential insider"
order by scripcd, Decision desc, px desc;
quit;

proc sql;
create table Insiders&scripcd as
select 
MONOTONIC() AS RowNum,
a.*
from intermed a, (select sum(decision) as Sumdesc from intermed) b
where calculated rownum <= max(30, b.Sumdesc);
quit;

