	create table ABTTempv3 as
	select distinct
	a.*, b.*, c.*, d.*, e.*, f.*, g.*, h.*, i.*, j.*,k.*
	from ((((((((((ABTTEMPEP a right outer join ABTTEMPAP b on a.panno = b.pannoap)
						left outer join ABTTEMPPE i on b.pannoap = i.pannoPE)
