						left outer join ABTTEMPPEB k on b.pannoap = k.pannoPEB)
						left outer join LGRUNAP c on b.pannoap = c.panno1Ap)
						left outer join LGRUNEP d on b.pannoap = d.panno1)
						left outer join NTRUNAP e on b.pannoap = e.panno1ap)
						left outer join NTRUNEP f on b.pannoap = f.panno1)
						left outer join STRUNAP g on b.pannoap = g.panno1ap)
						left outer join STRUNEP h on b.pannoap = h.panno1)
	;
quit;

