proc sql;
	create table ABTTempv4 as 
	select distinct
	a.*, 
	(&AnnDate - &ApFirst) as Preevnt,
	b.totsellqty as fSellqty,
	b.totsellval as fSellVol,
	b.trandate as Fselldt,
	(b.trandate - &APFirst) as dsfsell,
	(calculated dsfsell)/(calculated Preevnt) as EntrySell,
	c.totbuyqty as fBuyqty,
	c.totbuyval as fBuyVol,
	c.trandate as FBuydt,
	(c.trandate - &APFirst) as dsfbuy,
	(calculated dsfbuy)/(calculated Preevnt) as EntryBuy
	from ((ABTTempv3 a left outer join Periods b on a.pannoAP = b.panno)
						left outer join Periodb c on a.pannoAP = c.panno);
quit;

