%macro rank (dsn = Tab, wgt = W_QtyBAP, var = QtyBAP, 
rank = r_QtyBAP, quintiles = 100, OUT = temp);

PROC SQL ;
	CREATE TABLE temp AS SELECT &var,
	count(&var) as &wgt	 
	FROM &dsn 
	group by  &var
	ORDER BY  &var;
QUIT;

data _NULL_;
	set temp end=last;
	sumofwgt + &wgt;
	if last then call symput ('SUM', trim(left(put(sumofwgt, best.))));
run;

proc sort data = temp;
	by &VAR;
run;

data temp;
	set temp;
    drop sumofwgt;
	sumofwgt + &WGT;
	&rank = 0;
	do i = 1 to (&quintiles - 1) ;
		if ((&rank = 0) and (sumofwgt le ((symget('SUM') * i)/&quintiles))) 
		then &rank = i;
	end;
	if (&rank = 0) then &rank = &quintiles;
		drop i;
	*label &rank = "Rank for the Variable: &VAR.";
run;

proc sort data = &dsn;
	by &VAR;
run;


data &dsn(drop = &wgt);
	merge &dsn (in= a) temp (in=b);
	by &var;
	if a;
run;
%mend rank;

