proc sql;
	create table TestVal as
	select
		cat(PannoAP, scripcd) as IDTot,
		Pannoap,
		scripcd,
		FreqDSPE,
		NT_holdAP,
		lBuyqty,
		log(AVGOpenIntRatAP + 1) as logAVGOpenIntRatAP,
		log(AvgDaysHoldPO + 1) as logAvgDaysHoldPO,
		log(MaxDaysHoldPO + 1) as logMaxDaysHoldPO,
		log(MaxHoldIntsrtPE + 1) as logMaxHoldIntsrtPE,
		log(MinHoldIntrstPO + 1) as logMinHoldIntrstPO,
		r_FreqSTPEb,
		r_NT_hold,
		r_Netsell,
		r_QtySAP,
		QtySPEB, 
		QtyBPEB,
		QtySPE, 
		QtyBPE,
		QTYPEB/FreqPeb as AvgQtyPeb,
		r_val,
		r_valSAP,
		sum(freqSPEB, 0) as FreqPreS,
		notprof,
/*		***  Effect: FreqDSPE ;*/
		8.31937170814674 * FreqDSPE as eFreqDSPE,
/*		***  Effect: LGOpenRatio ;*/
		48.5856264389636 * NT_holdAP as eNT_holdAP,
/*		***  Effect: lBuyqty ;*/
		0.00009240505753 * lBuyqty as elBuyqty,
/*		***  Effect: logAVGOpenIntRatAP ;*/
		11.9837766690043 * calculated logAVGOpenIntRatAP as elogAVGOpenIntRatAP,
/*		***  Effect: logAvgDaysHoldPO ;*/
		-0.41578799362144 * calculated logAvgDaysHoldPO as elogAvgDaysHoldPO,
/*		***  Effect: logMaxDaysHoldPO ;*/
		-42.7180325497087 * calculated logMaxDaysHoldPO as elogMaxDaysHoldPO,
/*		***  Effect: logMaxHoldIntsrtPE ;*/
		1.35508322187386 * calculated logMaxHoldIntsrtPE as elogMaxHoldIntsrtPE,
/*		***  Effect: logMinHoldIntrstPO ;*/
		-2.08001664187221 * calculated logMinHoldIntrstPO as elogMinHoldIntrstPO,
/*		***  Effect: r_FreqSTPEb ;*/
		1.09569054782267 * r_FreqSTPEb as er_FreqSTPEb,
/*		***  Effect: r_NT_hold ;*/
		-1.9939777024078 * r_NT_hold as er_NT_hold,
/*		***  Effect: r_Netsell ;*/
		0.29173989939193 * r_Netsell as er_Netsell,
/*		***  Effect: r_QtySAP ;*/
		0.06488499557156 * r_QtySAP as er_QtySAP,
/*		*** Intercept;*/
		-4.17901979318692 as eIntercept
/*		insider*/
		from Modelv1
		where sum(freqSPEB, 0) >= 1  or r_valSAP >= 95
		order by scripcd;
quit;

proc sql;
	create table test as 
	select 
	*,
	case
		when sum(0, QtySPE, QtyBPE) > 0 and sum(0, QtySPEB, QtyBPEb) and QtySPe/sum(0, QtySPE, QtyBPE) < 0.5 and sum(QtySPEB, 0)/sum(0, QtySPEB, QtyBPEb) < 0.5 then "Less Sells than buys"
		when avgQTYPEb <= 500 then "Too Less qty Traded"
		when NotProf/100 < 50000 then "Not profitable enough" 
/*		when FreqSAP = 0 and fbuydt >= anndate then "only buy & after event"*/
/*		when FreqBAP = 0 and PropSell > 0.80 then "No buy and sell happen before event date"*/
/*		when FreqBAP = 0 and qtySPO = 0 then "only sell & before event"*/
/*		when PropBuyPO >= 0.60 then "mostly buy after event"*/
/*		when qtyBpo = 0 and QtySPO = 0 and PropBuyPE <= 0.75 then "mostly sell before event"*/
		else "potential insider"
	end as Waterfall,
	((8.31937170814674 * FreqDSPE) +
		(48.5856264389636 * NT_holdAP) +
		(0.00009240505753 * lBuyqty) +
		(11.9837766690043 * logAVGOpenIntRatAP) + 
		(48.9636747773025 * logAvgDaysHoldPO) +
		(-42.7180325497087 * logMaxDaysHoldPO) +
		(1.35508322187386 * logMaxHoldIntsrtPE) +
		(-2.08001664187221 * logMinHoldIntrstPO) +
		(1.09569054782267 * r_FreqSTPEb) +
		(-1.9939777024078 * r_NT_hold) +
		(0.29173989939193 * r_Netsell) +
		(-1.01689596238382 * r_QtySAP) +
		(-4.17901979318692)) as x,
	exp(calculated x) / (1 + exp(calculated x)) as px,
	case
		when calculated px >= 3/45 and calculated waterfall = "potential insider" then 1
		else 0
	END AS Decision
/*	case*/
/*		when calculated Decision = 0 then "Not Insider"*/
/*		when calculated Decision = 1 and PropBuyPE < 0.10 and PropBuyPO < 0.60 then "insider - possibly(sell)"*/
/*		when calculated Decision = 1 and qtyBpo = 0 and QtySPO = 0 and PropBuyPE > 0.75 then "insider - possibly(buy)"*/
/*		else "Insider proper"*/
/*	end as InsiderFine*/
	from TestVal
order by scripcd, Decision desc, px desc;
quit;


proc sql noprint;
create table intermed as
select * 
from test
where waterfall = "potential insider"
order by scripcd, Notprof desc, px desc;
quit;

proc sql;
create table Insiders&scripcd as
select 
MONOTONIC() AS RowNum,
a.*
from intermed a, (select sum(decision) as Sumdesc from intermed) b
where calculated rownum <= max(30, b.Sumdesc);
quit;

